import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }
  render() {
    const mystyle = {
      margin: "1%",
      padding: "3.5%",
      width: "18%",
      height: "120px",
      color: "black",
      backgroundColor: "gray",
      fontFamily: "Times New Roman",
      borderRadius: "0.3cm",
    };
    return <Button style={mystyle}>{this.props.val}</Button>;
  }
}
