import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }
  render() {
    const mystyle = {
      margin: "1%",
      paddingTop: "10%",
      width: "18%",
      backgroundColor:this.props.card.captainMode? "":" rgb(247, 214, 176)",
      borderRadius: "0.3cm",
      borderColor: "rgb(247, 214, 176)",
      position: "relative",
    };
    return (
      <Button style={mystyle}>
        <h4
          style={{
            fontWeight: "bold",
            fontFamily: "Guttman Vilna",
            backgroundColor: "white",
            color: "black",
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            marginLeft: "10%",
            marginRight: "10%",
            borderRadius: "0.3cm",

          }}
        >
          {this.props.card.val}
        </h4>
      </Button>
    );
  }
}
