import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }
  render() {
    const mystyle = {
      margin: "1%",
      padding: "3%",
      width: "15%",
      backgroundColor: " rgb(247, 214, 176)",
      fontFamily: "Aharoni",
      borderRadius: "0.3cm",
      
    };
    return (
      <Button style={mystyle}>
        <label>
          style={{
            fontWeight: "bold",
            fontSize: "50%",
            backgroundColor: "yellow",
            padding: "20%",
          }}
        >
          {this.props.val}
        </label>
      </Button>
    );
  }
}
