import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";

const belongs = {
  INITIAL: "initial",
  HOST: "host",
  HOSTED: "hosted",
  NEUTRAL: "neutral",
  BLACK: "black",
};
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }

  getBackgroundColor = (_belongsTo) => {
    switch (_belongsTo) {
      case belongs.HOST:
        return "rgb(196, 19, 19)";
        break;
      case belongs.HOSTED:
        return "rgb(38, 80, 170)";
        break;
      case belongs.NEUTRAL:
        return "rgb(214, 214, 95)";
        break;
      case belongs.BLACK:
        return "black";
        break;
      default:
    }
  };
  isEnabled = () => {
    if (this.props.card.belongsTo == belongs.INITIAL) return true;
else if(    this.props.isMyTurn )
return true? true : false;
  };
  render() {
    const mystyle = {
      margin: "1%",
      paddingTop: "10%",
      width: "18%",
      backgroundColor: this.props.captainMode
        ? this.getBackgroundColor(this.props.card.belongsTo)
        : " rgb(247, 214, 176)",
      borderRadius: "0.3cm",
      borderColor: "rgb(247, 214, 176)",
      position: "relative",
    };
    return (
      <Button style={mystyle} disabled={!this.isEnabled()}>
        <h4
          style={{
            fontWeight: "bold",
            fontFamily: "Guttman Vilna",
            backgroundColor: "white",
            color: "black",
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            marginLeft: "10%",
            marginRight: "10%",
            borderRadius: "0.3cm",
          }}
        >
          {this.props.card.value}
        </h4>
      </Button>
    );
  }
}
