import React, { Component } from "react";
import "../App.css";
import Button from "react-bootstrap/Button";
import RowOfCards from "./RowOfCards";
export default class Board extends Component {
  state = { CaptainMode: false };
  constructor(props) {
    super();
  }
  getRowsOfCards = () => {
    return this.props.board.rowsOfCards.map((rowOfCard) => (
      <RowOfCards cards={rowOfCard.cards}></RowOfCards>
    ));
  };

  changeToCaptainMode = (e) => {
    console.log("on changeToCaptainMode");
    this.setState({ CaptainMode: true });
  };
  changeBackToNormalMode = (e) => {
    console.log("on changeBackToNormalMode");
    this.setState({ CaptainMode: false });
  };

  componentDidMount = () => {};

  render() {
    let onChangeModeClick = this.state.CaptainMode
      ? this.changeBackToNormalMode
      : this.changeToCaptainMode;

    const ChangeModeStyle = {
      fontFamily: "Guttman Vilna",
      variant: "dark",
      size: "lg",
      onClick: onChangeModeClick
    };
    return (
      <React.Fragment>
        <Button style={ChangeModeStyle} onCl>Captain View</Button>
        <div className="board">{this.getRowsOfCards()}</div>
      </React.Fragment>
    );
  }
}
