import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Button";
import "../App.css";
import "./Card.css";
import Background from "../images/background_image.jpg";

const belongs = {
  INITIAL: "initial",
  HOST: "host",
  HOSTED: "hosted",
  NEUTRAL: "neutral",
  BLACK: "black",
};

const currentTurn = {
  NONE: "none",
  HOST: "host",
  HOSTED: "hosted",
};
export default class Card extends Component {
  state = { already_guessed: false };
  componentDidMount = () => {};
  constructor(props) {
    super();
  }

  getBackgroundColor = (_belongsTo) => {
    switch (_belongsTo) {
      case belongs.HOST:
        // if (this.props.captainMode)
        return "rgb(196, 19, 19)";
        // else if (this.state.already_guessed)
        //   return "https://upload.wikimedia.org/wikipedia/he/4/48/Codenames-hebrew-cover.jpg";
        break;
      case belongs.HOSTED:
        return "rgb(38, 80, 170)";
        break;
      case belongs.NEUTRAL:
        return "rgb(214, 214, 95)";
        break;
      case belongs.BLACK:
        return "black";
        break;
      default:
    }
  };
  isEnabled = () => {
    if (this.state.already_guessed) return false;
    if (this.props.currentTurn == currentTurn.NONE) return true;
    else {
      if (this.props.isHost && this.props.currentTurn == currentTurn.HOST)
        return true;
      else if (
        !this.props.isHost &&
        this.props.currentTurn == currentTurn.HOSTED
      )
        return true;
      else return false;
    }
  };
  getBackgroundImage = () => {
    if (this.props.captainMode) return "";
    else {
      return this.state.already_guessed ? "url(" + Background + ")" : "";
    }
  };
  onButtonClick = () => {
    this.setState({ already_guessed: true });
  };
  render() {
    const mystyle = {
      margin: "1%",
      paddingTop: "10%",
      width: "18%",
      backgroundColor: this.props.captainMode
        ? this.getBackgroundColor(this.props.card.belongsTo)
        : " rgb(247, 214, 176)",
      backgroundImage: this.getBackgroundImage(),
      backgroundSize: "cover",
      borderRadius: "0.3cm",
      borderColor: "rgb(247, 214, 176)",
      position: "relative",
    };
    return (
      <Col className={this.state.already_guessed ? "rotator" : ""}>
        <Button
          style={mystyle}
          disabled={!this.isEnabled()}
          onClick={this.onButtonClick}
        >
          <h4
            style={{
              fontWeight: "bold",
              fontFamily: "Guttman Vilna",
              backgroundColor: "white",
              color: "black",
              position: "absolute",
              bottom: 0,
              left: 0,
              right: 0,
              marginLeft: "10%",
              marginRight: "10%",
              borderRadius: "0.3cm",
              borderColor: "black",
              borderWidth: "5cm",
            }}
          >
            {this.props.card.value}
          </h4>
        </Button>
      </Col>
    );
  }
}
