import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }
  render() {
    const mystyle = {
      margin: "1%",
      padding: "3%",
      width: "18%",
      backgroundColor: " rgb(247, 214, 176)",
      fontFamily: "Aharoni",
      borderRadius: "0.3cm",
      textAlign
    };
    return (
      <Button style={mystyle}>
        <h2
          style={{
            // fontWeight: "bold",
            // fontSize: "50%",
            backgroundColor: "yellow",
            
            // marginTop: "70%",

            // padding: "20%",
          }}
        >
        </h2>
          {this.props.val}
      </Button>
    );
  }
}
