import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }
  render() {
    const mystyle = {
      margin: "1%",
      paddingTop: "10%",

      width: "18%",
      backgroundColor: " rgb(247, 214, 176)",
      borderRadius: "0.3cm",
      position: "relative",
    };
    return (
      <Button style={mystyle}>
        <h3
          style={{
            // fontWeight: "bold",
            // fontSize: "50%",
            fontFamily: "Calibri",

            backgroundColor: "white",
            color: "black",
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            //marginTop: "70%",

            // padding: "20%",
          }}
        >
          {this.props.val}
        </h3>
      </Button>
    );
  }
}
