import React, { Component } from "react";
import RowOfCards from "./RowOfCards";
import Button from "react-bootstrap/Button";
export default class Board extends Component {
  constructor(props) {
    super();
  }
  getRowsOfCards = () => {
    this.props.board.rowsOfCards.map((rowOfCard) =>
     <RowOfCards cards={rowOfCard.ca}>

     </RowOfCards>);
  };

  componentDidMount = () => {};

  render() {
    return (
      <React.Fragment>
        {/* <ScoreBoard></ScoreBoard>
        <CardRow></CardRow> */}
        <div>{this.getRowsOfCards()}</div>
      </React.Fragment>
    );
  }
}
