  constructor(props) {
    super();
  }
  getRowOfCards = () => {
    console.log(this.board);
    this.board.rowsOfCards.map((rowOfCard) => <RowOfCards></RowOfCards>);
  };

  componentDidMount = () => {};

  render() {
    return (
      <React.Fragment>
        {/* <ScoreBoard></ScoreBoard>
        <CardRow></CardRow> */}
        <div>{this.getRowOfCards()}</div>
      </React.Fragment>
    );
  }
}
