import React, { Component } from "react";
import "./App.css";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import io from "socket.io-client";
import Badge from "react-bootstrap/Badge";
import Spinner from "react-bootstrap/Spinner";
import Board from "./components/Board";

const status = {
  INITIAL: "initial",
  CREATED: "created",
  RUNNING: "running",
  FINISHED: "finished",
};
const currentTurn = {
  NONE: "none",
  HOST: "host",
  HOSTED: "hosted",
};

class App extends Component {
  state = {
    game_id: -1,
    game_name: "",
    user_id: "",
    user_name: "",
    status: status.INITIAL,
    board: [],
    captain_mode: false,
    is_host: false,
    start_button_clicked: false,
    current_turn: currentTurn.NONE,
  };
  // board = new Array(5);

  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.socket = io("http://localhost:3000");
    this.socket.on("INITIAL_USER_DATA", (data) => {
      this.setState({ user_id: data.user_id });
      console.log("success!!!!!! user id is:" + this.state.user_id);
    });
    this.socket.on("GAME_CREATED", (data) => {
      this.setState({ status: status.CREATED, game_id: data.game_id });
    });
    this.socket.on("RUN_GAME", (data) => {
      this.setState({ status: status.RUNNING, board: data.board });
    });
    this.socket.on("START_GAME", (data) => {
      this.setState({current });
    });
  }
  createNewGame = () => {
    this.setState({ is_host: true });
    this.socket.emit("CREATE_GAME", {
      game_name: this.state.game_name,
      user_id: this.state.user_id,
      user_name: this.state.user_name,
    });
  };
  ConnectGame = () => {
    this.socket.emit("CONNECT_GAME", {
      game_id: this.state.game_id,
      user_id: this.state.user_id,
      user_name: this.state.user_name,
    });
  };
  changeToCaptainMode = (e) => {
    console.log("on changeToCaptainMode");
    this.setState({ captain_mode: true });
  };

  changeBackToNormalMode = (e) => {
    console.log("on changeBackToNormalMode");
    this.setState({ captain_mode: false });
  };

  startButtonOnClick = (e) => {
    this.setState({ start_button_clicked: true });
    this.socket.emit("START_GAME", {
      game_id: this.state.game_id,
    });
  };
  render() {
    let onChangeModeClick = this.state.captain_mode
      ? this.changeBackToNormalMode
      : this.changeToCaptainMode;

    const ChangeModeStyle = {
      fontFamily: "Guttman Vilna",
      marginTop: "0.5%",
      fontWeight: "bold",
      fontSize: "150%",
    };
    return (
      <div className="App">
        <Image
          className={
            this.state.status === status.RUNNING ? "Img animator" : "Img"
          }
          src="https://upload.wikimedia.org/wikipedia/he/4/48/Codenames-hebrew-cover.jpg"
        />
        {this.state.status === status.INITIAL ? (
          <div>
            <Accordion className="Acc">
              <Card className="cards">
                <Card.Header>
                  <Accordion.Toggle as={Button} variant="info" eventKey="0">
                    Create Game!
                  </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="0">
                  <Card.Body className="CardBody">
                    <Form>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formGridUserName">
                          <Form.Label className="labels">User Name</Form.Label>
                          <Form.Control
                            placeholder="Enter user name"
                            onChange={(e) => {
                              this.setState({ user_name: e.target.value });
                            }}
                          />
                        </Form.Group>
                        <Form.Group as={Col} controlId="formGridRoomID">
                          <Form.Label className="labels">Room Name</Form.Label>
                          <Form.Control
                            placeholder="Enter room Name"
                            onChange={(e) => {
                              this.setState({ game_name: e.target.value });
                            }}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                    <Button
                      variant="success"
                      type="submit"
                      onClick={(e) => {
                        this.createNewGame();
                      }}
                    >
                      Submit
                    </Button>
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
              <Card className="cards">
                <Card.Header>
                  <Accordion.Toggle
                    className="ab"
                    as={Button}
                    variant="info"
                    eventKey="1"
                  >
                    Join Game!
                  </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="1">
                  <Card.Body className="CardBody">
                    <Form>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formGridUserName">
                          <Form.Label className="labels">User Name</Form.Label>
                          <Form.Control
                            placeholder="Enter user name"
                            onChange={(e) => {
                              this.setState({ user_name: e.target.value });
                            }}
                          />
                        </Form.Group>
                        <Form.Group as={Col} controlId="formGridRoomID">
                          <Form.Label className="labels">Room Id</Form.Label>
                          <Form.Control
                            placeholder="Enter room ID"
                            onChange={(e) => {
                              this.setState({ game_id: e.target.value });
                            }}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                    <Button
                      variant="success"
                      type="submit"
                      onClick={(e) => {
                        this.ConnectGame();
                      }}
                    >
                      Submit
                    </Button>
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
            </Accordion>
          </div>
        ) : null}
        {this.state.status === status.CREATED ? (
          <div>
            <h1>
              <Badge variant="success">
                Room #{this.state.game_id} had been created!
              </Badge>
            </h1>
            <h1>
              <Badge variant="success">Waiting for a player to join...</Badge>
            </h1>
            <Spinner className="sp" animation="grow" variant="danger" />
          </div>
        ) : null}
        {this.state.status === status.RUNNING ? (
          <div>
            <div>
              {this.state.is_host ? (
                !this.state.start_button_clicked ? (
                  <Button
                    style={ChangeModeStyle}
                    variant="success"
                    onClick={this.startButtonOnClick}
                  >
                    Start
                  </Button>
                ) : null
              ) : (
                <div>
                  <Badge style={ChangeModeStyle} variant="dark">
                    {this.state.current_turn == currentTurn.NONE
                      ? "Waiting for Host to start the game.."
                      : ""}
                  </Badge>
                  <Spinner animation="border" />
                </div>
              )}
            </div>
            <div>
              {this.state.current_turn == currentTurn.NONE ? this.state : null}
            </div>
            <div>
              <Button
                variant="dark"
                style={ChangeModeStyle}
                onClick={onChangeModeClick}
              >
                Captain View
              </Button>
              <Board
                board={this.state.board}
                captainMode={this.state.captain_mode}
              ></Board>
            </div>
          </div>
        ) : null}
      </div>
    );
  }
}
export default App;
