import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import "../App.css";

const belongs = {
  INITIAL: "initial",
  HOST: "host",
  HOSTED: "hosted",
  NEUTRAL: "neutral",
  BLACK: "black",
};
export default class Card extends Component {
  state = {};
  componentDidMount = () => {};
  constructor(props) {
    super();
  }

  getBackgroundColor = (_belongsTo) => {
    switch (_belongsTo) {
      case belongs.HOST:
        return ""
        break;
      case belongs.HOST:
        // code block
        break;
      case belongs.HOSTED:
        break;
      case belongs.NEUTRAL:
        break;
      case belongs.BLACK:
        return black
        break;
      default:
      // code block
    }
  };
  render() {
    const mystyle = {
      margin: "1%",
      paddingTop: "10%",
      width: "18%",
      backgroundColor: this.props.card.captainMode
        ? this.getBackgroundColor(this.props.card.belongsTo)
        : " rgb(247, 214, 176)",
      borderRadius: "0.3cm",
      borderColor: "rgb(247, 214, 176)",
      position: "relative",
    };
    return (
      <Button style={mystyle}>
        <h4
          style={{
            fontWeight: "bold",
            fontFamily: "Guttman Vilna",
            backgroundColor: "white",
            color: "black",
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            marginLeft: "10%",
            marginRight: "10%",
            borderRadius: "0.3cm",
          }}
        >
          {this.props.card.val}
        </h4>
      </Button>
    );
  }
}
