import React, { Component } from "react";
import "./App.css";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Image from "react-bootstrap/Image";
import io from "socket.io-client";
import Badge from "react-bootstrap/Badge";
import Spinner from "react-bootstrap/Spinner";

const status = {
  INITIAL: "initial",
  CREATED: "created",
  RUNNING: "running",
  DONE: "done",
};
class App extends Component {
  state = {
    game_id: -1,
    game_name: "",
    user_id: "",
    user_name: "",
    game_status: "",
    status: status.INITIAL,
  };
  constructor(props) {
    super(props);
  }
  componentDidMount() {
    this.socket = io("http://localhost:3000");
    this.socket.on("INITIAL_USER_DATA", (data) => {
      this.setState({ user_id: data.user_id });
      console.log("success!!!!!! user id is:" + this.state.user_id);
    });
    this.socket.on("GAME_CREATED", (data) => {
      this.setState({ status: status.CREATED, game_id: data.game_id });
    });
    this.socket.on("START_GAME", (data) => {
      this.setState({ status: status.RUNNING });
    });
  }
  createNewGame = () => {
    this.socket.emit("CREATE_GAME", {
      game_id: this.state.game_id,
      user_id: this.state.user_id,
      user_name: this.state.user_name,
    });
  };

  ConnectGame = () => {
    console.log(this.state.game_id);
    this.socket.emit("CONNECT_GAME", {
      game_id: this.state.game_id,
      user_id: this.state.user_id,
      user_name: this.state.user_name,
    });
  };

  render() {
    return (
      <div className="App">
        <Image
          className={
            this.state.status === status.RUNNING ? "Img animator" : "Img"
          }
          src="https://upload.wikimedia.org/wikipedia/he/4/48/Codenames-hebrew-cover.jpg"
        />
        {this.state.status === status.INITIAL ? (
          <div>
            <Accordion className="Acc">
              <Card className="cards">
                <Card.Header>
                  <Accordion.Toggle as={Button} variant="info" eventKey="0">
                    Create Game!
                  </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="0">
                  <Card.Body className="CardBody">
                    <Form>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formGridUserName">
                          <Form.Label className="labels">User Name</Form.Label>
                          <Form.Control
                            placeholder="Enter user name"
                            onChange={(e) => {
                              this.setState({ user_name: e.target.value });
                            }}
                          />
                        </Form.Group>
                        <Form.Group as={Col} controlId="formGridRoomID">
                          <Form.Label className="labels">Room Name</Form.Label>
                          <Form.Control
                            placeholder="Enter room Name"
                            onChange={(e) => {
                              this.setState({ game_name: e.target.value });
                            }}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                    <Button
                      variant="success"
                      type="submit"
                      onClick={(e) => {
                        this.createNewGame();
                      }}
                    >
                      Submit
                    </Button>
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
              <Card className="cards">
                <Card.Header>
                  <Accordion.Toggle
                    className="ab"
                    as={Button}
                    variant="info"
                    eventKey="1"
                  >
                    Join Game!
                  </Accordion.Toggle>
                </Card.Header>
                <Accordion.Collapse eventKey="1">
                  <Card.Body className="CardBody">
                    <Form>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formGridUserName">
                          <Form.Label className="labels">User Name</Form.Label>
                          <Form.Control placeholder="Enter user name"
                          onChange={(e) => {
                            this.setState({ user_name: e.target.value });
                          }} />
                        </Form.Group>
                        <Form.Group as={Col} controlId="formGridRoomID">
                          <Form.Label className="labels">Room Id</Form.Label>
                          <Form.Control placeholder="Enter room ID"
                          onChange={(e) => {
                            this.setState({ game: e.target.value });
                          }} />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                    <Button
                      variant="success"
                      type="submit"
                      onClick={(e) => {
                        this.ConnectGame();
                      }}
                    >
                      Submit
                    </Button>
                  </Card.Body>
                </Accordion.Collapse>
              </Card>
            </Accordion>
          </div>
        ) : null}
        {this.state.status === status.CREATED ? (
          <div>
            <h1>
              <Badge variant="success">
                Room #{this.state.game_id} had been created!
              </Badge>
            </h1>
            <h1>
              <Badge variant="success">Waiting for a player to join...</Badge>
            </h1>
            <Spinner className="sp" animation="grow" variant="danger" />
          </div>
        ) : null}
        {this.state.status === status.RUNNING ? <div></div> : null}
      </div>
    );
  }
}
export default App;
