import React, { Component } from "react";
import Button from "react-bootstrap/Button";
export default class Board extends Component {
  constructor(props) {
    super();
    this.rowsOfCards = [];
    console.log(props.board);
    for (let i = 0; i < 5; i++) {
      this.rowsOfCards.push(
        props.board.rowsOfCards[i].map((card) => <Button>{card.value}</Button>)
      );
    }
  }
  componentDidMount = () => {};

  render() {
    return (
      <React.Fragment>
        {/* <ScoreBoard></ScoreBoard>
        <CardRow></CardRow> */}
        <div>{this.rowsOfCards}</div>
      </React.Fragment>
    );
  }
}
