import React, { Component } from "react";
import "../App.css";
import Button from "react-bootstrap/Button";
import RowOfCards from "./RowOfCards";
export default class Board extends Component {
  state = {};
  constructor(props) {
    super();
  }
  getRowsOfCards = () => {
    return this.props.board.rowsOfCards.map((rowOfCard) => (
      <RowOfCards
        cards={rowOfCard.cards}
        captainMode={this.props.captainMode}
        isMyTurn={this.props.isMyTurn}
        currentTurn={this.state.current_turn}
      ></RowOfCards>
    ));
  };

  componentDidMount = () => {};

  render() {
    return (
      <React.Fragment>
        <div className="board">{this.getRowsOfCards()}</div>
      </React.Fragment>
    );
  }
}
