import React, { Component } from "react";
import Button from "react-bootstrap/Button";
export default class Board extends Component {
  constructor(props) {
    this.rowsOfCards = [];
    super();
  }
  componentDidMount = () => {
    for (let i = 0; i < 5; i++) {
      this.rowsOfCards.push(
        this.board.rowsOfCards[i].map((card) => <Button>{card.value}</Button>)
      );
    }
  };

  render() {
    return (
      <React.Fragment>
        {/* <ScoreBoard></ScoreBoard>
        <CardRow></CardRow> */}
        <div>{this.rowsOfCards}</div>
      </React.Fragment>
    );
  }
}
